import 'package:firebase_auth/firebase_auth.dart';String formatFirebaseAuthException(FirebaseAuthException e) {  switch (e.code) {    case 'invalid-email':      return 'عنوان بريد إلكتروني غير صالح';    case 'user-disabled':      return 'تم تعطيل حساب المستخدم';    case 'user-not-found':      return 'حساب المستخدم غير موجود';    case 'wrong-password':      return 'كلمة المرور غير صحيحة';    case 'email-already-in-use':      return 'عنوان البريد الإلكتروني قيد الاستخدام بالفعل';    case 'weak-password':      return 'كلمة المرور ضعيفة';    case 'requires-recent-login':      return 'يتطلب تسجيل الدخول مؤخرًا';    case 'network-request-failed':      return 'فشل طلب الشبكة';    case 'invalid-credential':      return 'اعتمادات غير صالحة';    case 'operation-not-allowed':      return 'العملية غير مسموح بها';    default:      return 'حدث خطأ غير معروف';  }}