import 'package:awesome_dialog/awesome_dialog.dart';import 'package:final_project/log_in.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';class Settings extends StatefulWidget {  const Settings({super.key});  @override  State<Settings> createState() => _SettingsState();}class _SettingsState extends State<Settings> {  @override  signOut() async {    AwesomeDialog(      context: context,      dialogType: DialogType.info,      animType: AnimType.rightSlide,      title: 'هل أنت متأكد من تسجيل الخروج',      titleTextStyle: TextStyle(        color: Colors.black,      ),      btnCancelOnPress: () {        Navigator.pop(context);      },      btnOkOnPress: () async {        await FirebaseAuth.instance.signOut().then((value) {          Navigator.pushReplacement(context, MaterialPageRoute(            builder: (context) {              return LogInPage();            },          ));        });      },      autoHide: Duration(milliseconds: 1200),    ).show();  }  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: Color.fromARGB(255, 4, 59, 153),        title: Text('Settings'),      ),      body: Padding(        padding: const EdgeInsets.all(15),        child: Column(          children: [            SizedBox(              height: 48,              width: double.infinity,              child: GestureDetector(                onTap: () {                  signOut();                },                child: Directionality(                  textDirection: TextDirection.rtl,                  child: ListTile(                    title: Text('تسجيل الخروج'),                    trailing: Icon(Icons.logout),                  ),                ),              ),            ),          ],        ),      ),    );  }}